import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Modal, Container } from 'react-bootstrap';

function App() {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [randomStory, setRandomStory] = useState(null);

  const coreValues = ["Kindness", "Respect", "Responsibility", "Positivity", "Integrity", "Belonging"];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://docs.google.com/spreadsheets/d/e/2PACX-1vS-G9c-yVcu8Qrgtu8kIM3UAFwWK8D2gYzpMFhC7nOKYznJHzxPby-9ZLRk_YXBsKMmhuXLrLFbxoAQ/pub?gid=480186919&single=true&output=tsv');
        const data = response.data.split('\n').map(row => row.split('\t'));
        const headers = data[0];
        const stories = data.slice(1).map(row => {
          let story = {};
          row.forEach((cell, index) => {
            story[headers[index]] = cell;
          });
          return story;
        });
        setStories(stories);
        setLoading(false);
      } catch (error) {
        setError(error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleButtonClick = (coreValue) => {
    const filteredStories = stories.filter(story => story['Which core value is this story associated with?'] === coreValue);
    const randomIndex = Math.floor(Math.random() * filteredStories.length);
    setRandomStory(filteredStories[randomIndex]);
    setShowModal(true);
  };

  const handleCloseModal = () => setShowModal(false);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error loading data: {error.message}</p>;

  return (
    <Container className="d-flex flex-column align-items-center mt-5">
      <h1>Ninth Grader Stories</h1>
      {coreValues.map((coreValue, index) => (
        <Button
          key={index}
          variant="outline-primary"
          className="mb-3"
          onMouseOver={e => e.target.classList.add('text-white', 'bg-primary')}
          onMouseOut={e => e.target.classList.remove('text-white', 'bg-primary')}
          onClick={() => handleButtonClick(coreValue)}
        >
          {coreValue}
        </Button>
      ))}

      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>{randomStory && randomStory['Who is your story about?']}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p><strong>Core Value:</strong> {randomStory && randomStory['Which core value is this story associated with?']}</p>
          <p>{randomStory && randomStory['Tell the story about the ninth grader. Include good detail that makes clear why this illustrates the core value you chose.']}</p>
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default App;
